mapper.select:
SELECT
	sr.ID,
	sr.STUDENTS_DATE,
	a.NAME,
	sr.LEARNING_CONTENTS,
	sr.UNDERSTANDING,
	sr.UNDERSTANDING_DETAIL,
	sr.TEACHER_SUPPORT,
	sr.QUESTION
FROM
	accounts a
inner JOIN
	studentsreports sr
on
	a.ID = sr.USER_ID


		a.CLIENT_NAME_ID = #{courseNameId}


		AND a.COURSE_NAME_ID = #{clientNameId}


		AND a.ID = #{id}


		AND sr.UNDERSTANDING = #{understanding}


		AND sr.TEACHER_SUPPORT = #{teacherSupport}


		AND sr.STUDENTS_DATE >= #{fromDate}


		AND sr.STUDENTS_DATE  <=  #{toDate}


;
